import { Component, Input, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import Swal from 'sweetalert2';
import { TemplateService } from 'src/app/qrtemplategenerator/services/template.service';
import { Section } from 'src/app/qrtemplategenerator/Models/Section';
import { Field } from 'src/app/qrtemplategenerator/Models/Field';

@Component({
  selector: 'app-template-details',
  templateUrl: './template-details.component.html',
  styleUrls: ['./template-details.component.css']
})
export class TemplateDetailsComponent implements OnInit {
  @Input() templateId!: number; 
  sectionParams!: FormGroup;
  fieldParams!: FormGroup;

  sections: Section[] = [];
  selectedSectionId: number | null = null;

  constructor(private fb: FormBuilder, private templateService: TemplateService) {}

  ngOnInit() {
    this.initSectionForm();
    this.initFieldForm();
    this.loadSections();
  }

  initSectionForm() {
    this.sectionParams = this.fb.group({
      id: [0],
      title: ['', [Validators.required, Validators.minLength(3), Validators.maxLength(100)]],
      type: ['', Validators.required],
    });
  }

  initFieldForm() {
    this.fieldParams = this.fb.group({
      id: [0],
      title: ['', [Validators.required, Validators.minLength(3), Validators.maxLength(100)]],
      type: ['', Validators.required],
      isRequired: [false, Validators.required],
    });
  }

  loadSections() {
    if (!this.templateId) return;

    this.templateService.getTemplateById(this.templateId.toString()).subscribe((template) => {
      this.sections = template.sections || [];
    });
  }

  editSection(section: Section | null = null) {
    this.selectedSectionId = section ? section.id : null;
    this.initSectionForm();
    if (section) {
      this.sectionParams.setValue({
        id: section.id,
        title: section.title,
        type: section.type,
      });
    }
  }

  saveSection() {
    if (this.sectionParams.invalid || !this.templateId) return;
    const section: Section = this.sectionParams.value;
    const sectionData = { title: section.title, type: this.mapSectionType(section.type) };

    if (section.id) {
      this.templateService.updateSection(this.templateId.toString(), section.id.toString(), sectionData).subscribe(() => {
        const existingSection = this.sections.find(s => s.id === section.id);
        if (existingSection) Object.assign(existingSection, section);
        this.showMessage('Section updated successfully.');
      });
    } else {
      this.templateService.addSection(this.templateId.toString(), sectionData).subscribe((newSection) => {
        this.sections.push(newSection);
        this.showMessage('Section added successfully.');
      });
    }
  }

  deleteSection(sectionId: number) {
    if (!this.templateId) return;

    this.templateService.deleteSection(this.templateId.toString(), sectionId.toString()).subscribe(() => {
      this.sections = this.sections.filter(s => s.id !== sectionId);
      this.showMessage('Section deleted successfully.');
    });
  }

  editField(section: Section, field: Field | null = null) {
    this.selectedSectionId = section.id;
    this.initFieldForm();
    if (field) {
      this.fieldParams.setValue({
        id: field.id,
        title: field.title,
        type: field.type,
        isRequired: field.isRequired,
      });
    }
  }

  saveField() {
    if (this.fieldParams.invalid || !this.selectedSectionId || !this.templateId) return;
    const field: Field = this.fieldParams.value;
    const fieldData = { title: field.title, type: this.mapFieldType(field.type), isRequired: field.isRequired };

    if (field.id) {
      this.templateService.updateField(this.templateId.toString(), this.selectedSectionId.toString(), field.id.toString(), fieldData).subscribe(() => {
        const section = this.sections.find(s => s.id === this.selectedSectionId);
        if (section) {
          const existingField = section.fields?.find(f => f.id === field.id);
          if (existingField) Object.assign(existingField, field);
        }
        this.showMessage('Field updated successfully.');
      });
    } else {
      this.templateService.addField(this.templateId.toString(), this.selectedSectionId.toString(), fieldData).subscribe((newField) => {
        const section = this.sections.find(s => s.id === this.selectedSectionId);
        if (section) {
          if (!section.fields) section.fields = [];
          section.fields.push(newField);
        }
        this.showMessage('Field added successfully.');
      });
    }
  }

  deleteField(section: Section, fieldId: number) {
    if (!this.templateId) return;

    this.templateService.deleteField(this.templateId.toString(), section.id.toString(), fieldId.toString()).subscribe(() => {
      section.fields = section.fields?.filter(f => f.id !== fieldId);
      this.showMessage('Field deleted successfully.');
    });
  }

  showMessage(msg: string) {
    Swal.fire({
      icon: 'success',
      title: msg,
      toast: true,
      position: 'top',
      showConfirmButton: false,
      timer: 3000
    });
  }

  private mapSectionType(type: string): number {
    return type === 'Table' ? 1 : 0;
  }

  private mapFieldType(type: string): number {
    const map: { [key: string]: number } = { 'Text': 0, 'Number': 1, 'Date': 2, 'Boolean': 3 };
    return map[type] ?? 0;
  }
}
