<div *ngIf="template">
    <h2>{{ template.title }}</h2>

    <!-- Back button when viewing section -->
    <div *ngIf="selectedSection" class="mb-4">
      <button class="btn btn-secondary" (click)="backToSections()">
        ← Back to Sections
      </button>
    </div>

    <!-- Sections List -->
    <div *ngIf="!selectedSection">
      <h3>Sections</h3>
      <button 
        *ngFor="let section of template.sections"
        class="section-card"
        (click)="viewSection(section)"
      >
        <h4>{{ section.title }}</h4>
        <p>Type: {{ section.type }}</p>
      </button>
    </div>

    <!-- Fields List -->
    <div *ngIf="selectedSection">
      <h3>Fields in {{ selectedSection.title }}</h3>
      <div *ngFor="let field of fields" class="field-card">
        <h5>{{ field.title }}</h5>
        <p>Type: {{ field.type }}</p>
        <p>Required: {{ field.isRequired ? 'Yes' : 'No' }}</p>
      </div>
    </div>
  </div>

  <div *ngIf="!template" class="loading">
    Loading...
  </div>
