import { Component, Input, ViewChild } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { ModalComponent } from 'angular-custom-modal';
import Swal from 'sweetalert2';
import { TemplateService } from 'src/app/qrtemplategenerator/services/template.service';
import { Template } from 'src/app/qrtemplategenerator/Models/Template';
import { Section } from 'src/app/qrtemplategenerator/Models/Section';
import { Field } from 'src/app/qrtemplategenerator/Models/Field';

@Component({
  selector: 'app-template-details',
  templateUrl: './template-details.component.html'
})
export class TemplateDetailsComponent {
  @Input() template!: Template;
  @ViewChild('addSectionModal') addSectionModal!: ModalComponent;
  @ViewChild('addFieldModal') addFieldModal!: ModalComponent;

  sectionParams!: FormGroup;
  fieldParams!: FormGroup;
  selectedSection?: Section;


    showMessage(msg: string, type: 'success' | 'error' = 'success') {
      Swal.fire({
        icon: type,
        title: msg,
        toast: true,
        position: 'top',
        showConfirmButton: false,
        timer: 3000,
        customClass: { container: 'toast' },
      });
    }

  constructor(private fb: FormBuilder, private templateService: TemplateService) {
    this.initSectionForm();
    this.initFieldForm();
  }

  initSectionForm() {
    this.sectionParams = this.fb.group({
      id: [0],
      title: ['', [Validators.required, Validators.minLength(3), Validators.maxLength(100)]],
      type: ['', Validators.required],
    });
  }

  initFieldForm() {
    this.fieldParams = this.fb.group({
      id: [0],
      title: ['', [Validators.required, Validators.minLength(3), Validators.maxLength(100)]],
      type: ['', Validators.required],
      isRequired: [false],
    });
  }

  // Section methods
  editSection(section?: Section) {
    this.sectionParams.reset({ id: 0 });
    if (section) {
      this.sectionParams.patchValue(section);
    }
    this.addSectionModal.open();
  }

  saveSection() {
    if (this.sectionParams.invalid) {
      this.showMessage('Please fill all required fields.', 'error');
      return;
    }

    const section: Section = this.sectionParams.value;
    const templateId = this.selectedTemplateId;



    if (templateId !== null) {
      const addSectionDTO = {
        title: section.title,
        type: this.mapSectionType(section.type) 
      };



      if (section.id) {

        this.templateService.updateSection(templateId.toString(), section.id.toString(), addSectionDTO)
          .subscribe(
            () => {
              const template = this.templateList.find(t => t.id === templateId);
              if (template && template.sections) {
                const existingSection = template.sections.find(s => s.id === section.id);
                if (existingSection) {
                  Object.assign(existingSection, section);
                }
              }
              this.showMessage('Section has been updated successfully.');
              this.addSectionModal.close();
            },
            (error) => this.handleErrorResponse(error)
          );
      } else {

        this.templateService.addSection(templateId.toString(), addSectionDTO)
          .subscribe(
            (newSection) => {
              const template = this.templateList.find(t => t.id === templateId);
              if (template) {
                if (!template.sections) {
                  template.sections = [];
                }
                template.sections.push(newSection);
              }
              this.showMessage('Section has been added successfully.');
              this.addSectionModal.close();
            },
            (error) => this.handleErrorResponse(error)
          );
      }
    }
  }


  private mapSectionType(type: any): number {
    const sectionTypeMap: { [key: string]: number } = {
      'Form': 0,
      'Table': 1,
    };
    return sectionTypeMap[type] ?? 0; 
  }
  private handleErrorResponse(error: any) {
    console.error('Error response:', error);
    console.error('Error details:', JSON.stringify(error.error));
    this.showMessage(error.error?.message || 'An unknown error occurred!', 'error');
  }


    showMessage(msg: string, type: 'success' | 'error' = 'success') {
      Swal.fire({
        icon: type,
        title: msg,
        toast: true,
        position: 'top',
        showConfirmButton: false,
        timer: 3000,
        customClass: { container: 'toast' },
      });
    }

  deleteSection(sectionId: number) {
    this.templateService.deleteSection(this.template.id.toString(), sectionId.toString())
      .subscribe(() => {
        this.template.sections = this.template.sections?.filter(s => s.id !== sectionId);
      });
  }

  // Field methods
  editField(section: Section, field?: Field) {
    this.selectedSection = section;
    this.fieldParams.reset({ id: 0 });
    if (field) {
      this.fieldParams.patchValue(field);
    }
    this.addFieldModal.open();
  }

  saveField() {
    if (this.fieldParams.invalid || !this.selectedSection) return;

    const field: Field = this.fieldParams.value;
    const sectionId = this.selectedSection.id;

    if (field.id) {
      this.templateService.updateField(this.template.id.toString(), sectionId.toString(), field.id.toString(), field)
        .subscribe(updated => this.updateFieldInList(updated));
    } else {
      this.templateService.addField(this.template.id.toString(), sectionId.toString(), field)
        .subscribe(newField => this.selectedSection?.fields?.push(newField));
    }
    this.addFieldModal.close();
  }

  deleteField(section: Section, fieldId: number) {
    this.templateService.deleteField(this.template.id.toString(), section.id.toString(), fieldId.toString())
      .subscribe(() => {
        section.fields = section.fields?.filter(f => f.id !== fieldId);
      });
  }

  private updateSectionInList(updated: Section) {
    const index = this.template.sections?.findIndex(s => s.id === updated.id) ?? -1;
    if (index > -1) this.template.sections![index] = updated;
  }

  private updateFieldInList(updated: Field) {
    const index = this.selectedSection?.fields?.findIndex(f => f.id === updated.id) ?? -1;
    if (index > -1) this.selectedSection!.fields![index] = updated;
  }

  toggleFields(section: Section) {
    section.expanded = !section.expanded;
    if (!section.fields) {
      this.templateService.getSectionFields(this.template.id.toString(), section.id.toString())
        .subscribe(fields => section.fields = fields);
    }
  }
}
