// template-details.component.ts
import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { TemplateService } from '../../services/template.service';
import { Template } from '../../Models/Template';
import { Section } from '../../Models/Section';
import { Field } from '../../Models/Field';

@Component({
  selector: 'app-template-details',
  templateUrl: './template-details.component.html',
  styleUrls: ['./template-details.component.css']
})
export class TemplateDetailsComponent implements OnInit {
  template!: Template;
  selectedSection: Section | null = null;
  fields: Field[] = [];

  constructor(
    private route: ActivatedRoute,
    private router: Router,
    private templateService: TemplateService
  ) {}

  ngOnInit() {
    this.route.paramMap.subscribe(params => {
      const templateId = params.get('id');
      const sectionId = params.get('sectionId');

      if (templateId) {
        this.loadTemplate(templateId);

        if (sectionId) {
          this.loadSection(templateId, sectionId);
        }
      }
    });
  }

  loadTemplate(id: string) {
    this.templateService.getTemplateWithSections(id).subscribe(template => {
      this.template = template;
      this.selectedSection = null;
    });
  }

  loadSection(templateId: string, sectionId: string) {
    this.templateService.getSectionWithFields(templateId, sectionId).subscribe(section => {
      this.selectedSection = section;
      this.fields = section.fields || [];
    });
  }

  viewSection(section: Section) {
    this.router.navigate([`/templates/${this.template.id}/sections/${section.id}`]);
  }

  backToSections() {
    this.router.navigate([`/templates/${this.template.id}`]);
  }
}
