export class TemplateDetailsComponent {
  constructor(fb, templateService) {
    this.fb = fb;
    this.templateService = templateService;
    this.template = {
      id: 0,
      title: '',
      uniqeCode: '',
      sections: []
    };
    this.sectionUpdated = new EventEmitter();
    this.selectedTemplateId = null;
    this.selectedSectionId = null;
    this.templateList = [];
    this.initSectionForm();
    this.initFieldForm();
  }
  ngOnInit() {
    // Any additional logic when the template details component is initialized
    console.log(this.template); // Log to ensure template data is correctly passed
  }

  ngOnChanges() {
    console.log('Template received:', this.template);
  }
  initSectionForm() {
    this.sectionParams = this.fb.group({
      id: [0],
      title: ['', [Validators.required, Validators.minLength(3), Validators.maxLength(100)]],
      type: ['', Validators.required]
    });
  }
  initFieldForm() {
    this.fieldParams = this.fb.group({
      id: [0],
      title: ['', [Validators.required, Validators.minLength(3), Validators.maxLength(100)]],
      type: ['', Validators.required],
      isRequired: [false]
    });
  }
  // Section methods
  editSection(templateId, section = null) {
    this.selectedTemplateId = templateId;
    this.addSectionModal.open();
    this.initSectionForm();
    if (section) {
      this.sectionParams.setValue({
        id: section.id,
        title: section.title,
        type: section.type
      });
    }
  }
  saveSection() {
    if (this.sectionParams.invalid) {
      this.showMessage('Please fill all required fields.', 'error');
      return;
    }
    const section = this.sectionParams.value;
    const templateId = this.selectedTemplateId;
    if (templateId !== null) {
      const addSectionDTO = {
        title: section.title,
        type: this.mapSectionType(section.type)
      };
      if (section.id) {
        this.templateService.updateSection(templateId.toString(), section.id.toString(), addSectionDTO).subscribe(() => {
          const template = this.templateList.find(t => t.id === templateId);
          if (template && template.sections) {
            const existingSection = template.sections.find(s => s.id === section.id);
            if (existingSection) {
              Object.assign(existingSection, section);
            }
          }
          this.showMessage('Section has been updated successfully.');
          this.addSectionModal.close();
        }, error => this.handleErrorResponse(error));
      } else {
        this.templateService.addSection(templateId.toString(), addSectionDTO).subscribe(newSection => {
          const template = this.templateList.find(t => t.id === templateId);
          if (template) {
            if (!template.sections) {
              template.sections = [];
            }
            template.sections.push(newSection);
          }
          this.showMessage('Section has been added successfully.');
          this.addSectionModal.close();
        }, error => this.handleErrorResponse(error));
      }
    }
  }
  mapSectionType(type) {
    const sectionTypeMap = {
      'Form': 0,
      'Table': 1
    };
    return sectionTypeMap[type] ?? 0;
  }
  handleErrorResponse(error) {
    console.error('Error response:', error);
    console.error('Error details:', JSON.stringify(error.error));
    this.showMessage(error.error?.message || 'An unknown error occurred!', 'error');
  }
  showMessage(msg, type = 'success') {
    Swal.fire({
      icon: type,
      title: msg,
      toast: true,
      position: 'top',
      showConfirmButton: false,
      timer: 3000,
      customClass: {
        container: 'toast'
      }
    });
  }
  deleteSection(templateId, sectionId) {
    this.templateService.deleteSection(templateId.toString(), sectionId.toString()).subscribe(() => {
      const template = this.templateList.find(t => t.id === templateId);
      if (template && template.sections) {
        template.sections = template.sections.filter(s => s.id !== sectionId);
      }
      this.showMessage('Section has been deleted successfully.');
    }, error => {
      this.showMessage(error, 'error');
    });
  }
  loadSections(templateId) {
    this.templateService.getTemplateById(templateId.toString()).subscribe(template => {
      const existingTemplate = this.templateList.find(t => t.id === templateId);
      if (existingTemplate) {
        existingTemplate.sections = template.sections;
      }
    });
  }
  getTemplateIdBySectionId(sectionId) {
    for (const template of this.templateList) {
      if (template.sections) {
        for (const section of template.sections) {
          if (section.id === sectionId) {
            return template.id;
          }
        }
      }
    }
    return undefined;
  }
  // Field methods
  editField(section, field = null) {
    this.selectedSectionId = section.id;
    const templateId = this.getTemplateIdBySectionId(section.id);
    this.selectedTemplateId = templateId !== undefined ? templateId : null;
    this.addFieldModal.open();
    this.initFieldForm();
    if (field) {
      this.fieldParams.setValue({
        id: field.id,
        title: field.title,
        type: field.type,
        isRequired: field.isRequired
      });
    }
  }
  saveField() {
    if (this.fieldParams.invalid) {
      this.showMessage('Please fill all required fields.', 'error');
      return;
    }
    const field = this.fieldParams.value;
    const sectionId = this.selectedSectionId;
    const templateId = this.selectedTemplateId;
    if (templateId != null && sectionId != null) {
      const addFieldDTO = {
        title: field.title,
        type: this.mapFieldType(field.type),
        isRequired: field.isRequired
      };
      if (field.id != null && field.id !== 0) {
        // In saveField() update case
        this.templateService.updateField(templateId.toString(), sectionId.toString(), field.id.toString(), addFieldDTO).subscribe(updatedField => {
          // Find and update the local field
          const template = this.templateList.find(t => t.id === templateId);
          const section = template?.sections?.find(s => s.id === sectionId);
          if (section?.fields) {
            const index = section.fields.findIndex(f => f.id === updatedField.id);
            if (index > -1) {
              section.fields = [...section.fields.slice(0, index), updatedField, ...section.fields.slice(index + 1)];
            }
          }
          this.showMessage('Field updated successfully');
          this.addFieldModal.close();
        }, error => this.handleErrorResponse(error));
      } else {
        // In saveField() add case
        this.templateService.addField(templateId.toString(), sectionId.toString(), addFieldDTO).subscribe(newField => {
