import { Routes } from '@angular/router';

// Components & Layouts
import { NotFoundComponent } from './shared/components/not-found/not-found.component';
import { AuthLayout } from './layouts/auth-layout';
import { AdminGuard } from './authentication/guards/admin.guard';
import { authGuard} from './authentication/guards/auth.guard';

const routes: Routes = [
  { path: '', redirectTo: '/auth/login', pathMatch: 'full' }, // Default route

  { 
    path: 'QRCodeGenerator', 
    loadChildren: () => import('./qrgenerator/qrgenerator.module')
                      .then(m => m.QRGeneratorModule),
    canActivate: [authGuard] // Allow both Application Users and Admins
  },
  { 
    path: 'auth',
    component: AuthLayout, // Apply the AuthLayout here
    loadChildren: () => import('./authentication/authentication.module')
                      .then(m => m.AuthenticationModule)
  },
  { 
    path: 'admin', 
    loadChildren: () => import('./qrtemplategenerator/qrtemplategenerator.module')
                      .then(m => m.QrtemplategeneratorModule),
    canActivate: [AdminGuard] // Allow only Admins
  },
  { path: '**', redirectTo: '/auth/login' } // Fallback route
];
