import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { TemplateService } from 'src/app/qrtemplategenerator/services/template.service';
import { Template, Section, Field } from 'src/app/qrtemplategenerator/Models';
import Swal from 'sweetalert2';

@Component({
  selector: 'app-template-details',
  templateUrl: './template-details.component.html',
  styleUrls: ['./template-details.component.css']
})
export class TemplateDetailsComponent implements OnInit {
  template!: Template;
  selectedSectionId: number | null = null;
  selectedTemplateId: number | null = null;

  constructor(
    private route: ActivatedRoute,
    private templateService: TemplateService
  ) { }

  ngOnInit(): void {
    const id = this.route.snapshot.paramMap.get('id');
    if (id) {
      this.loadTemplateDetails(+id);
    }
  }

  loadTemplateDetails(id: number) {
    this.templateService.getTemplateById(id.toString()).subscribe({
      next: (template) => {
        this.template = template;
        this.template.sections?.forEach(s => {
          s.expanded = false;
          if (s.fields) {
            s.fields.forEach(f => f.expanded = false);
          }
        });
      },
      error: (err) => Swal.fire('Error', 'Failed to load template details', 'error')
    });
  }

  toggleSection(section: Section) {
    if (!section.fields) {
      this.templateService.getSectionFields(
        this.template.id.toString(),
        section.id.toString()
      ).subscribe(fields => {
        section.fields = fields;
        section.expanded = !section.expanded;
      });
    } else {
      section.expanded = !section.expanded;
    }
  }

  toggleField(field: Field) {
    field.expanded = !field.expanded;
  }
}
