import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { BehaviorSubject, Observable, tap } from 'rxjs';

@Injectable({
  providedIn: 'root'
})
export class AuthService {
  private apiUrl = 'https://localhost:7110/api/Authenticate';
  private isLoggedInSubject = new BehaviorSubject<boolean>(this.checkLoginStatus());
  isLoggedIn$: Observable<boolean> = this.isLoggedInSubject.asObservable();

  constructor(private http: HttpClient , private router: Rou) {}

  login(data: any): Observable<any> {
    return this.http.post(`${this.apiUrl}/login`, data).pipe(
      tap((response: any) => {
        if (response?.token) {
          localStorage.setItem('authToken', response.token);
          localStorage.setItem('role', response.role); // Assuming the API returns user role
          localStorage.setItem('userName', response.name); // Store username

          this.setLoginStatus(true);

          // Redirect based on role
          if (response.role === 'Admin') {
            this.router.navigate(['/admin']);
          } else if (response.role === 'Application User') {
            this.router.navigate(['/QRCodeGenerator']);
          } else {
            this.router.navigate(['/auth/login']); // Default redirect
          }
        }
      })
    );
  }

  register(data: any): Observable<any> {
    return this.http.post(`${this.apiUrl}/register`, data); // Ensure this endpoint exists in the backend
  }

  setRole(role: string) {
    localStorage.setItem('role', role);
  }

  getRole(): string | null {
    return localStorage.getItem('role');
  }

  logout(): void {
    localStorage.removeItem('role');
    this.setLoginStatus(false);
    localStorage.removeItem('userName');  // Optionally remove the username as well
  }

  checkAuthenticationState() {
    this.setLoginStatus(this.checkLoginStatus());
  }

  getUserName(): string | null {
    return localStorage.getItem('userName');  // Adjust this based on your storage mechanism
  }

  getAuthHeaders(): { [key: string]: string } {
    const token = localStorage.getItem('authToken');  // Adjust this based on how you store the token
    return token ? { Authorization: `Bearer ${token}` } : {};
  }

  private setLoginStatus(isLoggedIn: boolean): void {
    this.isLoggedInSubject.next(isLoggedIn);
  }

  private checkLoginStatus(): boolean {
    return !!localStorage.getItem('role');
  }
}
