import { Component, inject, OnInit, ViewChild } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { ModalComponent } from 'your-modal-library'; // Replace with your modal library
import { TemplateService } from '../../services/template.service';
import { ActivatedRoute, Router } from '@angular/router';


@Component({
  selector: 'app-template-item',
  templateUrl: './template-item.component.html',
  styleUrls: ['./template-item.component.css']
})
export class TemplateItemComponent implements OnInit {

  _TemplateService=inject(TemplateService);
  _router=inject(ActivatedRoute);
  templateData: any = {};
  displayType: string = 'grid'; 
  templateId!: string;
  ngOnInit(): void {
    this.templateId = this._router.snapshot.paramMap.get('id')!; 
    this.getTemplatebyId();
  }


  initFieldForm(): void {
    this.fieldParams = this._fb.group({
      id: [null],
      title: ['', [Validators.required, Validators.minLength(3), Validators.maxLength(100)]],
      type: ['Text', Validators.required],
      isRequired: [false]
    });
  }

  getTemplatebyId(){
    this._TemplateService.getTemplateById(this.templateId).subscribe({
      next: (res) => {
        console.log( 'Template id',this.templateId);
        console.log('Result =' ,res);
        this.templateData = res;

      },
      error: (error) => {
        console.error('Error:', error);
      }


    })
  }

  toggleSection(section: any): void {
    section.expanded = !section.expanded;
  }

  toggleField(field: any): void {
    field.expanded = !field.expanded;
  }

  addSection(): void {
    const newSection = {
      title: '',
      type: this.mapSectionType('Form'), // Default type 'Form'
      fields: [],
      expanded: false
    };

    this._TemplateService.addSection(this.templateId, newSection).subscribe({
      next: (res) => {
        this.templateData.sections.push(res);
      },
      error: (error) => {
        console.error('Error adding section:', error);
      }
    });
  }

  // The mapSectionType method
  private mapSectionType(type: any): number {
    const sectionTypeMap: { [key: string]: number } = {
      'Form': 0,
      'Table': 1,
    };
    return sectionTypeMap[type] ?? 0; // Default to 0 if the type is not found
  }

  editSection(section: any): void {
    // Logic for opening a modal to edit a section (you can implement it with a modal library)
    // For example, if using the same modal system you had in `ListTemplateComponent`, you can call a modal for editing a section here.
  }

  deleteSection(sectionId: number): void {
    this._TemplateService.deleteSection(this.templateId, sectionId.toString()).subscribe({
      next: () => {
        this.templateData.sections = this.templateData.sections.filter((s: any) => s.id !== sectionId);
      },
      error: (error) => {
        console.error('Error deleting section:', error);
      }
    });
  }
// field methods 

addField(sectionId: number): void {
  const newField = {
    title: '',
    type: 'Text', // Default type 'Text'
    isRequired: false,
    expanded: false
  };

  // Convert sectionId to string before passing it to the service
  this._TemplateService.addField(this.templateId, sectionId.toString(), newField).subscribe({
    next: (res) => {
      const section = this.templateData.sections.find((s: any) => s.id === sectionId);
      if (section) {
        section.fields.push(res);
      }
    },
    error: (error) => {
      console.error('Error adding field:', error);
    }
  });
}

editField(sectionId: number, field: any): void {
  // Logic for opening a modal to edit a field (similarly, you can implement this part with a modal system).
}

deleteField(sectionId: number, fieldId: number): void {
  // Convert both sectionId and fieldId to strings before passing to the service
  this._TemplateService.deleteField(this.templateId, sectionId.toString(), fieldId.toString()).subscribe({
    next: () => {
      const section = this.templateData.sections.find((s: any) => s.id === sectionId);
      if (section) {
        section.fields = section.fields.filter((f: any) => f.id !== fieldId);
      }
    },
    error: (error) => {
      console.error('Error deleting field:', error);
    }
  });
}







}
