      <div *ngFor="let section of template.sections">
        <div class="section-item">
          <h4>{{ section.title }}</h4>
          <button class="btn btn-secondary" (click)="editSection(template.id, section)">Edit</button>
          <button class="btn btn-danger" (click)="deleteSection(template.id, section.id)">Delete</button>
