import { Component } from '@angular/core';

@Component({
    selector: 'app-root',
    templateUrl: './app.component.html',
})
export class AppComponent {
    constructor(private authService: AuthService, private router: Router) {
        this.redirectUser();
      }

      private redirectUser() {
        const isLoggedIn = this.authService.checkLoginStatus(); // âœ… Check if user is logged in
        const role = this.authService.getRole();

        if (!isLoggedIn) {
          this.router.navigate(['/auth/login']); // ðŸ”´ Redirect to login if not logged in
        } else {
          if (role === 'Admin') {
            this.router.navigate(['/admin']); // âœ… Admins go to Admin panel
          } else {
            this.router.navigate(['/QRCodeGenerator/qrgenerator']); // âœ… Users go to QR page
          }
        }
      }
}
